<div><h3>Sign in</h3><input id='aa_e'><button id='aa_req'>Request</button><input id='aa_c'><button id='aa_v'>Verify</button><pre id='aa_o'></pre></div><script>(function(){const $=id=>document.getElementById(id);$('aa_req').onclick=async()=>{$('aa_o').textContent=JSON.stringify(await window.AssetArcSSO.requestOTP($('aa_e').value));};$('aa_v').onclick=async()=>{$('aa_o').textContent=JSON.stringify(await window.AssetArcSSO.verifyOTP($('aa_e').value,$('aa_c').value),null,2); if(JSON.parse($('aa_o').textContent).ok) location.reload();};})();</script>